package org.hcjf.io.net.http;

import org.junit.Assert;
import org.junit.Test;

public class HttpPackageTest {

    @Test
    public void testHeaders() {

        String completeHeaderValue = "Bearer ";
        String complete = "";
        String yy =       "";
        String yyy =      "";
        String s  =       "";
        String s1 = "";

        String first =
                "GET /pub/WWW/TheProject.html HTTP/1.1\r\n" +
                "accept: */*\r\n" +
                "accept-encoding: gzip, deflate, br\r\n" +
                "accept-language: es-419,es;q=0.9\r\n" +
                "authorization: Bearer ";

        String second =
                "\r\n" +
                "content-type: application/json\r\n" +
                "origin: https://app-beta.sitrack.io\r\n" +
                "referer: https://app-beta.sitrack.io/dashboard/54db3091-2bae-4793-a6af-4c0427086047?END_DATE=2019-12-01%2023%3A59%3A00&LOGIN_CODE=0&REPORT_TYPE=0&START_DATE=2019-12-01%2000%3A00%3A00\r\n" +
                "sec-fetch-dest: empty\r\n" +
                "sec-fetch-mode: cors\r\n" +
                "sec-fetch-site: same-site\r\n" +
                "user-agent: Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/80.0.3987.149 Safari/537.36\r\n\r\n";

        HttpRequest request = new HttpRequest("/pub/WWW/TheProject.html", HttpMethod.GET);
        request.addData(first.getBytes());
        request.addData(second.getBytes());

        Assert.assertEquals(completeHeaderValue, request.getHeader("authorization").getHeaderValue());
        System.out.println(request.toString());
    }

}
